values.each do |comic_hash|
  comic = Comic.new(
    comic_id: comic_hash[:comic_id],
    title: comic_hash[:title],
    description: comic_hash[:description],
    comic_url: comic_hash[:comic_url],
    thumbnail: comic_hash[:thumbnail],
    sold_at: DateTime.parse(comic_hash[:sold_at])
  )

  comic.save

  comic_hash[:characters].each do |character_hash|
    character = Character.find_or_initialize_by(name: character_hash[:name])
    character.save if character.new_record?
    comic.characters << character unless comic.characters.any? { |c| c.name == character.name }
  end
end
